                     if(is.null(hydraulicka_vodivost()) || is.null(dodat_odpory()) || is.null(input$polomer_obsyp) || is.null(input$polomer_vrt_1)) 
                         return()
                     
                     else
                     {
                         
                     if(is.na(hydraulicka_vodivost()) || is.na(input$polomer_obsyp) || is.na(dodat_odpory()) || is.na(input$polomer_vrt_1))
                        return()
                     
                     else if(hydraulicka_vodivost() == 0 || dodat_odpory() == 0 || input$polomer_obsyp == 0 || input$polomer_vrt_1 == 0)
                     return()
                     
                     else
                         {
                             ko = as.numeric(hydraulicka_vodivost()*log(input$polomer_obsyp / input$polomer_vrt_1) /(dodat_odpory()+log((input$polomer_obsyp)/input$polomer_vrt_1)))
                     
